    {k8s_cluster="$k8s_cluster",tidb_cluster="$tidb_cluster"}
    [$__rate_interval]
)) by (type, priority)  / sum(rate(
    tikv_grpc_msg_duration_seconds_count
    {k8s_cluster="$k8s_cluster",tidb_cluster="$tidb_cluster"}
    [$__rate_interval]
)) by (type, priority) )

